import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProfileDropdown from './ProfileDropdown';
import './Navigation.css';

const Navigation = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        const profileElement = document.querySelector('.profile-icon, .user-name');
        if (profileElement && !profileElement.contains(event.target)) {
          setShowDropdown(false);
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setShowDropdown(!showDropdown);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-brand">
          <Link to="/" className="brand-logo">
            <div className="logo-icon">ðŸŽ‰</div>
            <span className="brand-text">EventHub</span>
          </Link>
        </div>

        <div className="nav-elements">
          <div className="nav-menu">
            <Link to="/events" className="nav-link">Events</Link>
            <a href="#about" className="nav-link">About</a>
            <a href="#contact" className="nav-link">Contact</a>
          </div>

          <div className="nav-actions">
            <div className="profile-section" ref={dropdownRef}>
              <div className="profile-icon" onClick={toggleDropdown}>
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                  <circle cx="12" cy="7" r="4"></circle>
                </svg>
              </div>
              <div className="user-name" onClick={toggleDropdown}>
                John Doe
              </div>
              {showDropdown && <ProfileDropdown />}
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
