import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Hero.css';
import './SignupForm.css';

const Hero = () => {
  const navigate = useNavigate();
  const [showSignupNav, setShowSignupNav] = useState(false);
  const signupRef = useRef(null);

  // Close signup when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (signupRef.current && !signupRef.current.contains(event.target)) {
        // Check if the click is not on the Get Started button
        const getStartedBtn = document.querySelector('.btn-secondary');
        if (getStartedBtn && !getStartedBtn.contains(event.target)) {
          setShowSignupNav(false);
        }
      }
    }

    if (showSignupNav) {
      document.addEventListener('mousedown', handleClickOutside);
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.body.style.overflow = 'auto';
    };
  }, [showSignupNav]);

  const handleGetStarted = (e) => {
    e.stopPropagation();
    setShowSignupNav(!showSignupNav);
    if (!showSignupNav) {
      window.scrollTo(0, 0);
    }
  };
  return (
    <section className="hero">
      <div className="hero-background">
        <div className="hero-overlay"></div>
      </div>

      <div className="hero-content">
        <div className="hero-container">
          <div className="hero-text">
            <h1 className="hero-title">
              Discover Amazing Events 
              <span className="highlight">Near You</span>
            </h1>
            <p className="hero-subtitle">
              Connect with your community through memorable experiences. Find, register, and attend events that matter to you.
            </p>

            <div className="hero-buttons">
              <button className="btn btn-primary">
                <span>Explore Events</span>
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M5 12h14M12 5l7 7-7 7"/>
                </svg>
              </button>
              <button className="btn btn-secondary" onClick={handleGetStarted}>
                <span>Get Started</span>
              </button>
              {showSignupNav && (
                <div className="signup-overlay">
                  <div className="signup-nav">
                    <div className="nav-brand">
                      <div className="brand-logo" onClick={() => setShowSignupNav(false)}>
                        <div className="logo-icon">ðŸŽ‰</div>
                        <span className="brand-text">EventHub</span>
                      </div>
                    </div>
                    <div className="nav-actions">
                      <div className="nav-link">Already have an account?</div>
                      <button className="btn btn-outline" onClick={(e) => {
                        e.stopPropagation();
                        setShowSignupNav(false);
                        // You can add navigation to login page here if needed
                      }}>Log in</button>
                    </div>
                  </div>
                  <div className="signup-content" ref={signupRef}>
                    <SignupForm onClose={() => setShowSignupNav(false)} />
                  </div>
                </div>
              )}
            </div>

            <div className="hero-stats">
              <div className="stat-item">
                <span className="stat-number">50K+</span>
                <span className="stat-label">Events</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">2M+</span>
                <span className="stat-label">Attendees</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">100+</span>
                <span className="stat-label">Cities</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="hero-scroll">
        <div className="scroll-indicator">
          <span>Scroll to explore</span>
          <div className="scroll-arrow">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M6 9l6 6 6-6"/>
            </svg>
          </div>
        </div>
      </div>
    </section>
  );
};

// SignupForm component
const SignupForm = ({ onClose }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log('Form submitted');
  };

  return (
    <div className="signup-form-container">
      <div className="signup-form">
        <div className="signup-header">
          <h2>Create an Account</h2>
          <p>Join our community of event enthusiasts</p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your full name"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Create a password"
              minLength="6"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Create Account
          </button>
        </form>
      </div>
    </div>
  );
};

export default Hero;
