import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navigation.css';

const Navigation = () => {
  const location = useLocation();

  // Show navigation on all pages
  const isLoggedIn = true; // This would come from your auth context/state in a real app

  if (location.pathname === '/signup' || location.pathname === '/login') {
    // Auth page navigation
    return (
      <nav className="signup-nav">
        <div className="nav-container">
          <div className="nav-brand">
            <Link to="/" className="brand-logo">
              <div className="logo-icon">ðŸŽ‰</div>
              <span className="brand-text">EventHub</span>
            </Link>
          </div>
          <div className="nav-actions">
            <div className="nav-link">
              {location.pathname === '/signup' ? 'Already have an account?' : "Don't have an account?"}
            </div>
            <Link 
              to={location.pathname === '/signup' ? '/login' : '/signup'} 
              className="btn btn-outline"
            >
              {location.pathname === '/signup' ? 'Log in' : 'Sign up'}
            </Link>
          </div>
        </div>
      </nav>
    );
  }

  // Main navigation for other pages
  return (
    <nav className="main-nav">
      <div className="nav-container">
        <div className="nav-brand">
          <Link to="/" className="brand-logo">
            <div className="logo-icon">ðŸŽ‰</div>
            <span className="brand-text">EventHub</span>
          </Link>
        </div>
        <div className="nav-elements">
          <div className="nav-menu">
            <Link to="/events" className="nav-link">Events</Link>
            <a href="#about" className="nav-link">About</a>
            <a href="#contact" className="nav-link">Contact</a>
          </div>
          <div className="nav-actions">
            {isLoggedIn ? (
              <div className="profile-section">
                <div className="profile-icon">ðŸ‘¤</div>
                <span className="profile-name">John Doe</span>
              </div>
            ) : (
              <>
                <Link to="/signup" className="btn btn-outline">Sign Up</Link>
                <Link to="/login" className="btn btn-primary">Log In</Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );

};

export default Navigation;
