import React, { useEffect, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navigation.css';

const Navigation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [showSignupNav, setShowSignupNav] = useState(false);

  // Handle signup page state
  useEffect(() => {
    if (location.pathname === '/signup') {
      setShowSignupNav(true);
    } else {
      setShowSignupNav(false);
    }
  }, [location.pathname]);

  // Don't render navigation on signup page
  if (showSignupNav) {
    return (
      <nav className="signup-nav">
        <div className="nav-container">
          <div className="nav-brand">
            <div className="brand-logo" onClick={() => navigate('/')}>
              <div className="logo-icon">ðŸŽ‰</div>
              <span className="brand-text">EventHub</span>
            </div>
          </div>
          <div className="nav-actions">
            <div className="nav-link">Already have an account?</div>
            <button className="btn btn-outline" onClick={() => navigate('/login')}>
              Log in
            </button>
          </div>
        </div>
      </nav>
    );
  }

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-brand">
          <Link to="/" className="brand-logo">
            <div className="logo-icon">ðŸŽ‰</div>
            <span className="brand-text">EventHub</span>
          </Link>
        </div>

        <div className="nav-elements">
          <div className="nav-menu">
            <Link to="/events" className="nav-link">Events</Link>
            <a href="#about" className="nav-link">About</a>
            <a href="#contact" className="nav-link">Contact</a>
          </div>

          <div className="nav-actions">
            <div className="profile-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                <circle cx="12" cy="7" r="4"></circle>
              </svg>
            </div>
            <div className="user-name">
              John Doe
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
